// https://api.openweathermap.org/data/2.5/weather?q=dhanbad&appid=12c095d850be2d67dd3412d25061fd89

import React, {useState, useEffect} from 'react';
import './saif.css';

const Temp = () => {
  const [searchValue, setSearchValue] = useState("dhanbad");
  const [tempInfo, setTempInfo] = useState("");


  const getWeatherCondition = async () => {
    try {
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&units=metric&appid=12c095d850be2d67dd3412d25061fd89`;

      const res = await fetch(url);
      const data = await res.json();

      const {temp, humidity, pressure} = data.main;
      const {main: weathermood } = data.weather[0];
      const {name} = data;
      const {speed} = data.wind;
      const {country, sunrise, sunset} = data.sys;

      const myNewWeatherInfo = {
        temp,
        humidity,
        pressure,
        weathermood,
        name,
        speed,
        country,
        sunrise,
        sunset,
      };
      
      setTempInfo(myNewWeatherInfo)
      // console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getWeatherCondition();
  }, []);
  
  return (
    <>
        <div className="wrap">
            <div className="search">
              <input type="search"
              placeholder="search..."
              autoFocus
              id="search"
              className="searchTerm"
              value={searchValue}
              onChange={(e) => setSearchValue(e.target.value)}
               />
               <button className="searchButton" type="button"
                 onClick={getWeatherCondition}>
                 Search
               </button>
            </div>
        </div>
        {/* {our temp card} */}
        <article className="widget">
          <div className="weatherIcon">
            <i className={"wi wi-day-sunny"}></i>
          </div>
          <div className="weatherInfo">
            <div className="temperature">
              <span>25.5&deg;</span>
            </div>
            <div className="description">
              <div className="weatherCondition">sunny</div>
              <div className="place">Dhanbad, India</div>
            </div>
          </div>
          <div className="date"> { new Date().toLocaleString()} </div>
          {/* { our 4 column section } */}
          <div className="extra-temp">
            <div className="temp-info-minmax">
              <div className="two-sided-section">
                <p><i className={"wi wi-sunset"}></i>
                </p>
                <p className="extra-info-leftside">
                  19:19 PM <br />
                  Sunset
                </p>
              </div>
              <div className="two-sided-section">
                <p><i className={"wi wi-humidity"}></i>
                </p>
                <p className="extra-info-leftside">
                  19:19 PM <br />
                  Humidity
                </p>
              </div>
            </div>
            <div className="weather-extra-info">
            <div className="two-sided-section">
                <p><i className={"wi wi-rain"}></i>
                </p>
                <p className="extra-info-leftside">
                  19:19 PM <br />
                  Pressure
                </p>
              </div>
              <div className="two-sided-section">
                <p><i className={"wi wi-strong-wind"}></i>
                </p>
                <p className="extra-info-leftside">
                  19:19 PM <br />
                  Speed
                </p>
              </div>
            </div>
          </div>
        </article>
    </>
  )
}

export default Temp
